package arrays;

public class TwoDiamentionalArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		
	String[][] data= new String[3][2];
	//row1
	data[0][0]="user1";
	data[0][1]="password1";
	
	//row2
	data[1][0]="user2";
	data[1][1]="password2";
	
	//row3
	data[2][0]="user2";
	data[2][1]="password2";
	

	System.out.println(data[2][0]);
	
	
	}

}
